// Parse GPT-4 response
let content = $input.item.json.message.content;

// Remove markdown code blocks if present
content = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

try {
  const parsed = JSON.parse(content);
  return {
    json: {
      product: $node['Select Random Product'].json,
      ad: parsed,
      timestamp: new Date().toISOString()
    }
  };
} catch (e) {
  // Fallback if parsing fails
  return {
    json: {
      product: $node['Select Random Product'].json,
      ad: {
        caption: content.substring(0, 200),
        hashtags: ['wellness', 'selfcare', 'mindfulness', 'lunarituals', 'shopsmall'],
        image_prompt: `Professional product photography of ${$node['Select Random Product'].json.name}, aesthetic wellness brand, soft lighting`
      },
      timestamp: new Date().toISOString()
    }
  };
}

