// Update product stats
const fs = require('fs');
const productsPath = '/home/user/luna-ritual-studio/automation/data/products.json';
const statsPath = '/home/user/luna-ritual-studio/automation/data/post-stats.json';

// Update products.json
const products = JSON.parse(fs.readFileSync(productsPath, 'utf-8'));
const product = $node['Select Random Product'].json;
const productIndex = products.findIndex(p => p.id === product.id);

if (productIndex !== -1) {
  products[productIndex].last_posted = new Date().toISOString();
  products[productIndex].total_posts = (products[productIndex].total_posts || 0) + 1;
  fs.writeFileSync(productsPath, JSON.stringify(products, null, 2));
}

// Update post-stats.json
const stats = JSON.parse(fs.readFileSync(statsPath, 'utf-8'));
const adContent = $node['Parse JSON Response'].json.ad;
const imagePath = $node['Save Image to Disk'].json.path;

stats.posts.push({
  id: 'local_' + Date.now(),
  product_id: product.id,
  product_name: product.name,
  platform: 'instagram',
  caption: adContent.caption,
  hashtags: adContent.hashtags,
  image_path: imagePath,
  posted_at: new Date().toISOString(),
  status: 'generated',
  engagement: 0
});

stats.last_updated = new Date().toISOString();
fs.writeFileSync(statsPath, JSON.stringify(stats, null, 2));

console.log('âœ… Post generated successfully!');
console.log('Product:', product.name);
console.log('Image:', imagePath);
console.log('Caption:', adContent.caption.substring(0, 100) + '...');

return {
  json: {
    success: true,
    product_name: product.name,
    image_path: imagePath,
    caption: adContent.caption,
    hashtags: adContent.hashtags
  }
};
