let products = $json;

// Handle different data structures
if (!Array.isArray(products)) {
  if (products && products.products) {
    products = products.products;
  } else if (products) {
    products = [products];
  } else {
    // No products data, return error
    return [{ json: { error: 'No products found', id: 'fallback-001', name: 'Fallback Product', title: 'Fallback Product', category: 'General', description: 'Default product', price: 10, image: 'product-candle-1.jpg', tags: ['default'] } }];
  }
}

if (!Array.isArray(products) || products.length === 0) {
  return [{ json: { error: 'Empty products array', id: 'fallback-001', name: 'Fallback Product', title: 'Fallback Product', category: 'General', description: 'Default product', price: 10, image: 'product-candle-1.jpg', tags: ['default'] } }];
}

const now = new Date();
const available = products.filter(p => {
  if (!p.last_posted) return true;
  const days = (now - new Date(p.last_posted)) / (1000*60*60*24);
  return days > 3;
});

const pool = available.length ? available : products;
const selected = pool[Math.floor(Math.random() * pool.length)] || products[0];

return [{ json: selected }];

const product = $json;
const imageMap = {
  'aff-001': 'affirmation-rest',
  'aff-002': 'affirmation-joy',
  'aff-003': 'affirmation-abundance',
  'cnd-001': 'product-candle-1',
  'cnd-002': 'product-candle-2',
  'fsh-015': 'product-slip-dress'
};
const hintBase = imageMap[product.id] || (product.image || '').replace(/\.[^.]+$/,'') || '';
return [{ json: { product, hintBase } }];

// Get images from GitHub src/assets directory
const owner = 'Tyrizle851';
const repo = 'luna-ritual-studio';
const ref = 'main';
const baseRaw = `https://raw.githubusercontent.com/${owner}/${repo}/${ref}`;

// Get all input items - GitHub API returns array of files
const allInputs = $input.all();
const allFiles = [];

// Extract files from input
for (const item of allInputs) {
    const data = item.json;

    // Handle if data is directly the array from GitHub API
    if (Array.isArray(data)) {
        for (const file of data) {
            if (file && file.type === 'file' && /\.(png|jpe?g|webp)$/i.test(file.name)) {
                allFiles.push({ name: file.name, path: file.path });
            }
        }
    }
    // Handle if data is a single file object
    else if (data && data.type === 'file' && /\.(png|jpe?g|webp)$/i.test(data.name)) {
        allFiles.push({ name: data.name, path: data.path });
    }
}

// Get product info
const prod = $node['Map to Image Hint'].json.product || {};
const hintBase = $node['Map to Image Hint'].json.hintBase || '';
const id = (prod.id || '').toLowerCase();
const name = (prod.name || prod.title || '').toLowerCase();

// Score each file
function score(file) {
    const base = file.name.replace(/\.[^.]+$/, '').toLowerCase();
    let s = 0;
    if (hintBase && base.includes(hintBase.toLowerCase())) s += 5;
    if (id && base.includes(id)) s += 3;
    if (name) {
        const tokens = name.split(/[^a-z0-9]+/).filter(Boolean);
        for (const t of tokens) {
            if (t.length > 2 && base.includes(t)) s += 1;
        }
    }
    if (/\.png$/i.test(file.name)) s += 0.2;
    if (/\.(jpg|jpeg)$/i.test(file.name)) s += 0.15;
    return s;
}

// Find best match
let best = null;
for (const f of allFiles) {
    const s = score(f);
    if (!best || s > best.s) {
        best = { ...f, s };
    }
}

// Build URL
let url = null;
if (best) {
    url = `${baseRaw}/${best.path}`;
} else {
    // Fallback to default image
    url = `${baseRaw}/src/assets/product-candle-1.jpg`;
}

return [{ json: {
    resolved_image_url: url,
    picked: best,
    candidates_count: allFiles.length
} }];

