  }
  content = content.replace(/```json\n?/g,'').replace(/```/g,'').trim();
  const parsed = JSON.parse(content);
  return [{ json: {
    product: $node['Map to Image Hint'].json.product,
    title: parsed.title,
    description: parsed.description,
    alt_text: parsed.alt_text,
    hashtags: parsed.hashtags,
    image_prompt: parsed.image_prompt
  }}];
} catch (e) {
  const base = $node['Map to Image Hint'].json;
  const product = base.product || {};
  return [{ json: {
    product: product,
    title: product.name || product.title || 'Product',
    description: product.description || 'Discover your new ritual.',
    alt_text: product.name || product.title || 'Product image',
    hashtags: ['#wellness','#selfcare','#lunarituals'],
    image_prompt: `Professional product photo of ${product.name || product.title || 'product'}. Vertical composition, soft natural lighting, minimalist background. Pinterest-ready.`
  }}];
}

const clamp = (s,n)=> (s||'').toString().trim().slice(0,n);
const o = { ...$json };
o.title = clamp(o.title || 'Product', 100);
o.description = clamp(o.description || 'Discover your new ritual.', 500);
o.alt_text = clamp(o.alt_text || o.title, 250);
let tags = Array.isArray(o.hashtags) ? o.hashtags : [];
tags = tags.map(t => (t||'').toString().trim())
  .filter(Boolean)
  .map(t => t.startsWith('#') ? t.toLowerCase() : ('#'+t.toLowerCase()))
  .slice(0,5);
o.hashtags = tags.length ? tags : ['#wellness','#selfcare','#lunarituals'];
o.image_prompt = clamp(o.image_prompt || `Professional product photo of ${o.title}. Vertical composition, soft natural lighting, minimalist background. Keep product identical to reference.`, 1000);
return [{ json: o }];

// Check if Probe Models succeeded
if ($json.error || !$json.data) {
  return [{ json: { canImage: false, reason: 'API check failed' } }];
}

const models = ($json.data || []).map(m => m.id);
const hasDallE = models.some(m => m.includes('dall-e'));
return [{ json: { canImage: hasDallE, models_checked: models.length } }];

