// Try to get generated image URL and capture detailed error info
let genUrl = null;
let dalleError = null;
let dalleStatus = "not_attempted";

try {
  const dalleNode = $node['Generate Image'];

  if (!dalleNode) {
    dalleError = "Generate Image node didn't execute";
    dalleStatus = "node_not_executed";
  } else if (dalleNode.json) {
    const dalleData = dalleNode.json;

    // Check for OpenAI API error response
    if (dalleData.error) {
      dalleError = `OpenAI API Error: ${dalleData.error.message || JSON.stringify(dalleData.error)}`;
      dalleStatus = "api_error";

      // Capture specific error details
      if (dalleData.error.code) {
        dalleError += ` (Code: ${dalleData.error.code})`;
      }
      if (dalleData.error.type) {
        dalleError += ` (Type: ${dalleData.error.type})`;
      }
    }
    // Check for successful response
    else if (dalleData.data && dalleData.data[0] && dalleData.data[0].url) {
      genUrl = dalleData.data[0].url;
      dalleStatus = "success";
    }
    // Unknown response format
    else {
      dalleError = `Unexpected response format: ${JSON.stringify(dalleData).substring(0, 200)}`;
      dalleStatus = "unexpected_response";
    }
  } else {
    dalleError = "Generate Image node has no data";
    dalleStatus = "no_data";
  }
} catch (e) {
  dalleError = `Exception: ${e.message}`;
  dalleStatus = "exception";
}

// Get resolved image from GitHub
const resolved = $node['Resolve via GitHub']?.json?.resolved_image_url;

// Use generated if available, otherwise use resolved
const finalUrl = genUrl || resolved || 'https://raw.githubusercontent.com/Tyrizle851/luna-ritual-studio/main/src/assets/product-candle-1.jpg';

return [{ json: { 
  final_image_url: finalUrl, 
  generated_used: !!genUrl,
  has_resolved: !!resolved,
  dalle_status: dalleStatus,
  dalle_error: dalleError,
  dalle_attempted: true
} }];

